import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import "./../styles/management.css"
import "./../styles/sideBar.css"
import SideBar from './SideBar';
import TimePicker from './TimePicker';
import TextForm from './TextForm';
import $ from 'jquery'
import ActivityRow from './ActivityRow';




export default class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            activeItemIndex: 0
        }
        this.changeContent = this.changeContent.bind(this)
    }

    changeContent(i) {
        this.setState({
            activeItemIndex: i
        })
    }

    render() {
        let contentShown
        if (this.state.activeItemIndex == 0) {
            contentShown = <><ActivityRow/></>
        } else {
            contentShown = <p>Ciao mondo</p>
        }
        return (
            <div id="containerDiv" className="row g-0">
                <div id="sidebarDiv" className="col-3">
                    <SideBar onClick={this.changeContent} activeItemIndex={this.state.activeItemIndex} />
                </div>
                <div id="mainContentContainer" className="col">
                    {contentShown}
                </div>
            </div>
        )
    }
}
