import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import "./../styles/pages.css"
import $ from 'jquery';
import ActivityRow from './ActivityRow';

function renderActivityRows(activityRows) {
    return activityRows.map((ActivityRow, index) => {
        return (
            <ActivityRow />
        )
    });
}

export default class ActivityTable extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            activityRows: [<ActivityRow key="0" />],
            lastKey: 0
        }
        this.addActivityRow = this.addActivityRow.bind(this)
    }

    addActivityRow() {
        this.setState({
            activityRows: [...this.state.activityRows, <ActivityRow key={this.state.lastKey + 1} />],
            lastKey: this.state.lastKey + 1
        });
    }

    render() {
        return (
            <><div>
                <table id="activitiesTable" className="table">
                    <thead>
                        <tr>
                            <div className="row">
                                <th className="col-1 headerRow" scope="col">Nome attività</th>
                                <th className="col-2 headerRow" scope="col">Fascia oraria</th>
                                <th className="col-1 headerRow" cope="col">Preavviso minimo</th>
                                <th className="col-1 headerRow" scope="col">Numero coperti</th>
                                <th className="col-3 headerRow" scope="col">Giorni</th>
                                <th className="col-1 headerRow" scope="col">Elimina</th>
                            </div>
                        </tr>
                    </thead>
                    <tbody>
                        <tr></tr>
                        {this.state.activityRows}
                    </tbody>
                </table>
            </div>
                <div id="buttonsDiv">

                    <button id="saveChangesButton" type="button" className="btn btn-primary">Salva modifiche</button>
                    <button onClick={this.addActivityRow} id="addActivityButton" type="button" className="btn btn-outline-primary">Aggiungi attività</button>
                </div></>
        )
    }
}
