import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import "./../styles/pages.css"
import $ from 'jquery';
import ActivityRow from './ActivityRow';

function renderActivityRows(activityRows) {
    return activityRows.map((ActivityRow, index) => {
        return (
            <ActivityRow />
        )
    });
}

export default class ActivityTable extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            activityRows: []
        }

    }

    render() {
        let row = <ActivityRow/>
        this.state.activityRows.concat([row])
        return (
            <div>
                <table id="activitiesTable" className="table">
                    <thead>
                        <tr>
                            <th scope="col">Nome attivit√†</th>
                            <th scope="col">Fascia oraria</th>
                            <th scope="col">Preavviso minimo</th>
                            <th scope="col">Numero coperti</th>
                            <th scope="col">Giorni</th>
                            <th scope="col">Elimina</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr></tr>
                        {this.state.activityRows}
                    </tbody>
                </table>
            </div>
        )
    }
}
