import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import MenuItem from './MenuItem';

export default  class SideBar extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            activeItemIndex: 0
        }
    }

    changeActiveItem(i) {
        this.setState({
            activeItemIndex: i
        })
    }

    render() {
        return (
            <ul className="nav nav-pills flex-column mb-auto">
                <MenuItem
                    itemName="customization"
                    className={this.state.activeItemIndex == 0 ? "nav-link active" : "nav-link link-dark"}
                    itemIcon="bi bi-brush"
                    itemDisplayName="Personalizza"
                    onClick={this.changeActiveItem} />
                <MenuItem
                    itemName="services"
                    className={this.state.activeItemIndex == 1 ? "nav-link active" : "nav-link link-dark"}
                    itemIcon="bi bi-share"
                    itemDisplayName="Servizi" />
                <MenuItem
                    itemName="services"
                    className={this.state.activeItemIndex == 2 ? "nav-link active" : "nav-link link-dark"}
                    itemIcon="bi bi-share"
                    itemDisplayName="Servizi" />
                <MenuItem
                    itemName="services"
                    className={this.state.activeItemIndex == 3 ? "nav-link active" : "nav-link link-dark"}
                    itemIcon="bi bi-share"
                    itemDisplayName="Servizi" />
                <MenuItem
                    itemName="services"
                    className={this.state.activeItemIndex == 4 ? "nav-link active" : "nav-link link-dark"}
                    itemIcon="bi bi-share"
                    itemDisplayName="Servizi" />
            </ul>
        )
    }
}
