import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import "./../styles/pages.css"
import $ from 'jquery';
import TimePicker from './TimePicker';
import TextForm from './TextForm';
import DayPicker from './DayPicker';

export default class ActivityRow extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        return (
            <div>
                <tr>
                    <th scope="row">
                        <TextForm placeholder="es. Cena" />
                    </th>
                    <td>
                        Dalle:
                        <TimePicker min="00:00" max="23:59" />
                        Alle:
                        <TimePicker min="00:00" max="23:59" />
                    </td>
                    <td>
                        <div style="position: relative">
                            <input class="form-control" type="time" id="appt" name="appt" min="09:00" max="18:00" required></input>
                        </div>
                    </td>
                    <td>
                        <TimePicker min="00:00" max="23:59" />
                    </td>
                    <td>
                        <DayPicker />
                    </td>
                    <td>
                        <button type="button" class="deleteActivityButton btn btn-outline-danger">
                            <i class="bi bi-trash"></i>
                        </button>
                    </td>
                </tr>
            </div>
        )
    }
}
