import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import "./../styles/pages.css"
import $ from 'jquery';
import ActivityRow from './ActivityRow';

function renderActivityRows(activityRows) {
    return activityRows.map((ActivityRow, index) => {
        return (
            <ActivityRow />
        )
    });
}

export default class ActivityTable extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            activityRows: [<ActivityRow key="0" />],
            lastKey: 0
        }
    }

    addActivityRow() {
        this.setState(previousState => ({
            activityRows: [...previousState.activityRows, <ActivityRow key={this.previousState.lastKey + 1} />],
            lastKey: lastKey + 1
        }));
    }

    render() {
        return (
            <><div>
                <table id="activitiesTable" className="table">
                    <thead>
                        <tr>
                            <th scope="col">Nome attività</th>
                            <th scope="col">Fascia oraria</th>
                            <th scope="col">Preavviso minimo</th>
                            <th scope="col">Numero coperti</th>
                            <th scope="col">Giorni</th>
                            <th scope="col">Elimina</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr></tr>
                        {this.state.activityRows}
                    </tbody>
                </table>
            </div><div id="buttonsDiv">
                    <button id="saveChangesButton" type="button" class="btn btn-primary">Salva modifiche</button>
                    <button onClick={this.addActivityRow} id="addActivityButton" type="button" class="btn btn-outline-primary">Aggiungi attività</button>
                </div></>
        )
    }
}
